mapper.select:
SELECT B.*, TO_CHAR(CDATETIME, 'YYYY-MM-DD') AS CDATE
FROM TBL_BOARD B
WHERE BOARDNO = #{boardNo}

mapper.update:
UPDATE TBL_BOARD
SET TITLE = #{title}, CONTENTS = #{contents}, UDATETIME = SYSDATE
WHERE BOARDNO = #{boardNo}

UPDATE TBL_BOARD
SET CNT = CNT + 1
WHERE BOARDNO = #{boardNo}

mapper.delete:
DELETE FROM TBL_BOARD
WHERE BOARDNO IN

	#{item}
